import time

def compute_public_value(g, a, p):
    """
    Compute the public value A = g^a mod p for Diffie-Hellman key exchange
    
    Args:
        g: The generator value
        a: The secret exponent
        p: The prime modulus
    
    Returns:
        The public value A = g^a mod p
    """
    start_time = time.time()
    result = pow(g, a, p)
    end_time = time.time()
    
    print(f"Computation completed in {end_time - start_time:.4f} seconds")
    return result

